import { Button, Dialog, DialogActions, DialogTitle, IconButton } from "@material-ui/core";
import React from "react";
import ReactDOM from "react-dom"
import { connect } from "react-redux";
import styled from "styled-components";
import TrelloButton from "./TrelloButton";
import TrelloForm from "./TrelloForm";

const BootstrapDialog = styled(Dialog)(({ theme }) => ({
    '& .MuiDialogContent-root': {
        padding: theme.spacing(2),
    },
    '& .MuiDialogActions-root': {
        padding: theme.spacing(1),
    },
}));

const BootstrapDialogTitle = (props) => {
    const { children, onClose, ...other } = props;

    return (
        <DialogTitle sx={{ m: 0, p: 2 }} {...other}>
            {children}
            {onClose ? (
                <IconButton
                    aria-label="close"
                    onClick={onClose}
                    sx={{
                        position: 'absolute',
                        right: 8,
                        top: 8,
                        color: (theme) => theme.palette.grey[500],
                    }}
                >
                    {/* <CloseIcon /> */}
                </IconButton>
            ) : null}
        </DialogTitle>
    );
};

const ModalEditForm = (open, text, handleChange, closeForm, saveCard) => {
    if (!open) return null

    const handleClose = () => {
        // setOpen(false);
    };

    return ReactDOM.createPortal(
        <div>
            <BootstrapDialog
                onClose={handleClose}
                aria-labelledby="customized-dialog-title"
                open={open}
            >
                <BootstrapDialogTitle id="customized-dialog-title" onClose={handleClose}>
                    Modal title
                </BootstrapDialogTitle>
                {/* <DialogContent dividers>
                    <Typography gutterBottom>
                        Cras mattis consectetur puru
                    </Typography>
                </DialogContent> */}
                <DialogActions>
                    <TrelloForm text={text} onChange={handleChange} closeForm={closeForm}>
                        <TrelloButton onClick={saveCard}>Save</TrelloButton>
                    </TrelloForm>
                    <Button autoFocus onClick={handleClose}>
                        Save changes
                    </Button>
                </DialogActions>
            </BootstrapDialog>
        </div>
        , document.body)
}


export default connect()(ModalEditForm);
